<script lang="ts">
    let media: string[] = [
        'data:image/jpeg;base64,',
        'https://static.wikia.nocookie.net/megamitensei/images/b/b8/Persona_5_Confidant_Guides_Icon_%28Judgment%29_-_Sae_Niijima.png',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6Wcw06RDnQbkdqJyK0s901GFIva28QlutLA&s',
    ];
    
    let currentIndex = 0;

    function showNext() {
        currentIndex = (currentIndex + 1) % media.length;
    }

    function showPrevious() {
        currentIndex = (currentIndex - 1 + media.length) % media.length;
    }
</script>

<div class="slider-container">
    <button on:click={showPrevious} class="nav-button">&#60;</button>
    {#each media as url, index}
        {#if index === currentIndex}
            <div class="image-container">
                <img src={url} alt={`Image ${index + 1}`} class="image">
            </div>
        {/if}
    {/each}
    <button on:click={showNext} class="nav-button">&#62;</button>
</div>

